mapper.insert:
INSERT INTO EMPLOYEE (
                       EMP_NAME
                     , EMP_NO
                     , EMAIL
                     , PHONE
)
VALUES
    (
      #{ name }
    , #{ no }
    , #{ email }
    , #{ phone }
    )

mapper.update:
UPDATE EMPLOYEE
SET   EMP_NAME = #{ name }
  , EMP_NO = #{ no }
  , EMAIL = #{ email }
  , PHONE = #{ phone }
WHERE EMP_ID = #{ id }

mapper.delete:
DELETE
FROM EMPLOYEE
WHERE EMP_ID = #{ id }
