mapper.insert:
INSERT INTO EMPLOYEE (
                      EMP_ID
                      , EMP_NAME
                     , EMP_NO
                     , EMAIL
                     , PHONE
                     , JOB_CODE
                     , SAL_LEVEL
)
VALUES
    (
     #{id}
    , #{name}
    , #{no}
    , #{email}
    , #{phone}
    , #{jobCode}
    , #{salLevel}
    )

mapper.update:
UPDATE EMPLOYEE
SET   EMP_NAME = #{ name }
  , EMP_NO = #{ no }
  , EMAIL = #{ email }
  , PHONE = #{ phone }
  ,JOB_CODE = #{jobCode}
  ,SAL_LEVEL = #{salLevel}
WHERE EMP_ID = #{ id }

mapper.delete:
DELETE
FROM EMPLOYEE
WHERE EMP_ID = #{ id }
